<?php/**  +------------------------------------------------------------------------------ * 控制器基类-织梦二次开发网  +------------------------------------------------------------------------------ * @category   SEOapp * @package  Common * @version  $Id$ * @url   http://www.dedevvip.com  +------------------------------------------------------------------------------ */  class Base {    var $dsql;    var $tpl;    var $dtp;    var $request;        function __construct()    {        global $dsql;        $this->dsql = $dsql;        $this->tpl = new DedeTemplate();        $this->dtp = new DataListCP();    }        function SetVar($k,$v)    {        $this->tpl->SetVar($k, $v);    }        function Assign($k,$v)    {        $this->tpl->Assign($k, $v);    }        function SetArray($k,$v)    {        $this->tpl->SetArray($k, $v);    }        function LoadTemplate($tmpfile)    {        $this->tpl->LoadTemplate($tmpfile);    }        function Display()    {        $this->tpl->Display();    }         }